// of the Apache-2.0 license. See the LICENSE file for details.
describe('storage', () => {
  it('should return well known keys', () => {
    expect(typeof _storage.storage.substrate).toBe('object');
    expect(_storage.storage.substrate.authorityCount).toBeTruthy();
    expect(_storage.storage.substrate.authorityPrefix).toBeTruthy();
    expect(_storage.storage.substrate.changesTrieConfig).toBeTruthy();
    expect(_storage.storage.substrate.code).toBeTruthy();
    expect(_storage.storage.substrate.extrinsicIndex).toBeTruthy();
    expect(_storage.storage.substrate.heapPages).toBeTruthy();
  });
});