"use strict";

var _interopRequireDefault = require("@babel/runtime/helpers/interopRequireDefault");

var _static = _interopRequireDefault(require("@polkadot/extrinsics/static"));

var _Method = _interopRequireDefault(require("./Method"));

// Copyright 2017-2019 @polkadot/extrinsics authors & contributors
// This software may be modified and distributed under the terms
// of the Apache-2.0 license. See the LICENSE file for details.
describe('Method', () => {
  beforeEach(() => {
    _Method.default.injectMethods(_static.default);
  });
  it('handles decoding correctly (bare)', () => {
    expect(new _Method.default({
      args: [],
      callIndex: [1, 2]
    }).toU8a()).toEqual(new Uint8Array([1, 2, 0])); // array of 0
  });
  it('handles creation from a hex value properly', () => {
    expect(new _Method.default('0x0102').toU8a()).toEqual(new Uint8Array([1, 2, 0])); // array of 0 for consensus.noteOffline
  });
  describe('hasOrigin', () => {
    const test = {
      args: [],
      callIndex: [1, 2]
    };
    it('is false with no arguments', () => {
      expect(new _Method.default(test, {
        args: []
      }).hasOrigin).toEqual(false);
    });
    it('is false with first argument as non-Origin', () => {
      expect(new _Method.default(test, {
        args: [{
          name: 'a',
          type: 'u32'
        }]
      }).hasOrigin).toEqual(false);
    });
    it('is false with first argument as non-Origin', () => {
      expect(new _Method.default(test, {
        args: [{
          name: 'a',
          type: 'Origin'
        }]
      }).hasOrigin).toEqual(true);
    });
  });
});