// of the Apache-2.0 license. See the LICENSE file for details.
describe('ChainProperties', () => {
  it('decodes from a null value', () => {
    expect([...new _ChainProperties.default(null).entries()]).toEqual([]);
  });
  it('decodes from an actual object', () => {
    expect([...new _ChainProperties.default({
      decimals: 15,
      tokenSymbol: 'BBQ'
    }).entries()]).toEqual([['decimals', 15], ['tokenSymbol', 'BBQ']]);
  });
  it('checks equality', () => {
    expect(new _ChainProperties.default({
      decimals: 15,
      tokenSymbol: 'BBQ'
    }).eq([['tokenSymbol', 'BBQ'], ['decimals', 15]])).toBe(true);
  });
});