"use strict";

var _interopRequireDefault = require("@babel/runtime/helpers/interopRequireDefault");

var _Vote = _interopRequireDefault(require("./Vote"));

// Copyright 2017-2019 @polkadot/types authors & contributors
// This software may be modified and distributed under the terms
// of the Apache-2.0 license. See the LICENSE file for details.
describe('Vote', () => {
  it('constructs via boolean true', () => {
    expect(new _Vote.default(true).toNumber()).toEqual(-1);
  });
  it('constructs via boolean false', () => {
    expect(new _Vote.default(false).toNumber()).toEqual(0);
  });
  it('has isYay for positive', () => {
    expect(new _Vote.default(new Boolean(true)).isAye).toBe(true);
  });
  it('has isYay for negative', () => {
    expect(new _Vote.default(new Boolean(false)).isNay).toBe(true);
  });
  it('is positive for negative numbers', () => {
    expect(new _Vote.default(-999).isAye).toBe(true);
  });
  it('is negative for positive numbers', () => {
    expect(new _Vote.default(999).isNay).toBe(true);
  });
});